import React, { useState } from 'react';
import { Card } from 'primereact/card';
import { InputText } from 'primereact/inputtext';
import { Button } from 'primereact/button';
import { Password } from 'primereact/password';
import { TabMenu } from 'primereact/tabmenu';
import { Menubar } from 'primereact/menubar';

const LoginPage = () => {
    const [activeIndex, setActiveIndex] = useState(0);
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');

    const menuItems = [
        { label: 'Home' },
        { label: 'Our products' },
        { label: 'About US' },
        { label: 'Contact US' }
    ];

    const tabItems = [
        {label: 'Login', icon: 'pi pi-fw pi-sign-in'},
        {label: 'Sign up', icon: 'pi pi-fw pi-user-plus'}
    ];

    const start = <i className="pi pi-hospital" style={{ fontSize: '1.5rem', color: 'var(--primary-color)' }}></i>;
    const end = <Button label="Join" severity="primary" />;

    const containerStyle = {
        background: 'var(--surface-ground)',
        minHeight: '100vh'
    };

    const mainContentStyle = {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        minHeight: '100vh',
        padding: '6rem 2rem 2rem 2rem'
    };

    const formStyle = {
        width: '100%',
        maxWidth: '450px'
    };

    const inputContainerStyle = {
        display: 'flex',
        flexDirection: 'column',
        gap: '1.5rem'
    };

    const forgotLinkStyle = {
        color: 'var(--primary-color)',
        textDecoration: 'none',
        fontSize: '0.875rem',
        cursor: 'pointer',
        marginTop: '0.5rem',
        display: 'block'
    };

    return (
        <div style={containerStyle}>
            <div style={{ position: 'fixed', top: 0, width: '100%', zIndex: 1000 }}>
                <Menubar 
                    model={menuItems} 
                    start={start} 
                    end={end}
                    style={{ background: 'var(--surface-card)', border: 'none' }}
                />
            </div>

            <div style={mainContentStyle}>
                <div style={formStyle}>
                    <Card>
                        <TabMenu 
                            model={tabItems} 
                            activeIndex={activeIndex} 
                            onTabChange={(e) => setActiveIndex(e.index)}
                            style={{ marginBottom: '2rem' }}
                        />
                        
                        <div style={inputContainerStyle}>
                            <span className="p-input-icon-left w-full">
                                <i className="pi pi-envelope" />
                                <InputText
                                    value={email}
                                    onChange={(e) => setEmail(e.target.value)}
                                    placeholder="Email or phone number"
                                    style={{ width: '100%' }}
                                />
                            </span>

                            <Password
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                placeholder="Password"
                                toggleMask
                                feedback={false}
                                style={{ width: '100%' }}
                                inputStyle={{ width: '100%' }}
                            />

                            <a style={forgotLinkStyle}>
                                Forgot your password?
                            </a>

                            <Button 
                                label="Login" 
                                severity="primary"
                                style={{ width: '100%' }}
                            />
                        </div>
                    </Card>
                </div>
            </div>
        </div>
    );
};

export default Login;
