import React, { useState } from "react";
import "primereact/resources/themes/lara-light-cyan/theme.css";
import "primereact/resources/primereact.min.css";
import "primeicons/primeicons.css";
import RegistrarCliente from "../Modals/RegistrarCliente";

const Navbar = () => {
  const [isModalVisible, setIsModalVisible] = useState(false);
  const [menuActive, setMenuActive] = useState(false); // Estado para alternar menú

  const handleCloseModal = () => {
    setIsModalVisible(false);
  };

  const handleRegister = (formData) => {
    console.log("Datos registrados:", formData);
    handleCloseModal();
  };

  return (
    <>
      <div className="login-wrapper">
        <div className="nav">
          {/* Menú de hamburguesa antes del logo */}
          <button
            className="nav-toggle"
            onClick={() => setMenuActive(!menuActive)}
            aria-label="Toggle menu"
          >
            <i className="pi pi-bars"></i>
          </button>

          {/* Logo */}
          <div className="logo" style={{ display: "flex", alignItems: "center" }}>
            <img
              src="/img/navbar.png"
              alt="Logo"
              style={{ width: "40px", height: "40px", marginRight: "10px" }}
            />
            <span style={{ fontWeight: "bold", fontSize: "1.5rem", color: "#005f73" }}>
              Ouro Bank
            </span>
          </div>

          {/* Menú de navegación */}
          <div className={`nav-links ${menuActive ? "active" : ""}`}>
            <a href="/" className="nav-link">
              Inicio
            </a>
            <a href="/IniciarSesion" className="nav-link">
              Iniciar Sesión
            </a>
            <a href="/RegistroAnalista" className="nav-link">
              Contáctenos
            </a>
          </div>

          <RegistrarCliente
            visible={isModalVisible}
            onHide={handleCloseModal}
            onRegister={handleRegister}
            className="join-btn"
          />
        </div>
      </div>

      <style jsx>{`
        /* Estilo para el menú de hamburguesa */
        .nav {
          display: flex;
          justify-content: space-between;
          align-items: center;
        }

        .nav-toggle {
          background: none;
          border: none;
          color: #005f73;
          font-size: 2rem;
        }

        .logo {
          display: flex;
          align-items: center;
        }

        .nav-links {
          display: flex;
          gap: 20px;
        }

        .nav-links a {
          color: #005f73;
          text-decoration: none;
          font-size: 1.2rem;
        }

        .nav-links.active {
          display: block;
        }

        .nav-links {
          display: none;
          position: absolute;
          top: 60px;
          right: 0;
          background-color: white;
          box-shadow: 0px 4px 6px rgba(0, 0, 0, 0.1);
          padding: 20px;
          width: 200px;
          border-radius: 8px;
          z-index: 1;
        }

   
      `}</style>
    </>
  );
};

export default Navbar;
