import React, { useState, useRef } from 'react';
import { Toast } from 'primereact/toast'; // Asegúrate de importar Toast

// Simulación de una función para insertar el préstamo (puedes reemplazarla con la llamada a tu API)
const insertarPrestamo = async (prestamo) => {
  return new Promise((resolve, reject) => {
    setTimeout(() => {
      // Simulación de un error en la inserción del préstamo
      const errorSimulado = true; // Cambia esto a false para simular un éxito
      if (errorSimulado) {
        reject("Error al insertar el préstamo"); // Simula un error
      } else {
        resolve("Préstamo insertado correctamente"); // Simula éxito
      }
    }, 1000); // Simula tiempo de espera de 1 segundo
  });
};

const FormularioPrestamo = () => {
  // Estado para los datos del formulario
  const [formData, setFormData] = useState({
    monto: '',
    plazo: '',
    // Otros campos si es necesario
  });

  const toast = useRef(null); // Referencia al Toast de PrimeReact

  // Función para validar el formulario
  const validateForm = () => {
    return formData.monto && formData.plazo; // Verifica que ambos campos no estén vacíos
  };

  // Función para manejar el envío del formulario
  const handleSubmit = async (e) => {
    e.preventDefault(); // Previene el comportamiento por defecto de submit
    if (validateForm()) {
      try {
        // Crear el objeto del préstamo
        const prestamoNuevo = {
          ...formData,
          estadoPrestamo: 2, // Estado del préstamo, ajusta según tu lógica
        };

        // Llamada a la función para insertar el préstamo
        const resultado = await insertarPrestamo(prestamoNuevo);

        // Mostrar mensaje de éxito en consola y con Toast
        console.log("Resultado de la inserción:", resultado);
        toast.current.show({
          severity: 'success',
          summary: 'Éxito',
          detail: 'Préstamo registrado con éxito',
          life: 3000, // Duración del mensaje en milisegundos
        });
      } catch (error) {
        // Manejo del error
        console.error("Error al insertar el préstamo:", error);
        toast.current.show({
          severity: 'error',
          summary: 'Error',
          detail: error, // Muestra el mensaje de error
          life: 3000, // Duración del mensaje en milisegundos
        });
      }
    } else {
      // Si la validación falla, mostrar advertencia
      toast.current.show({
        severity: 'warn',
        summary: 'Advertencia',
        detail: 'Por favor, complete todos los campos',
        life: 3000, // Duración del mensaje en milisegundos
      });
    }
  };

  return (
    <div>
      {/* Componente Toast para mostrar mensajes */}
      <Toast ref={toast} />

      {/* Formulario para insertar el préstamo */}
      <form onSubmit={handleSubmit}>
        <div>
          <label>Monto</label>
          <input
            type="number"
            value={formData.monto}
            onChange={(e) => setFormData({ ...formData, monto: e.target.value })}
            required
          />
        </div>

        <div>
          <label>Plazo</label>
          <input
            type="number"
            value={formData.plazo}
            onChange={(e) => setFormData({ ...formData, plazo: e.target.value })}
            required
          />
        </div>

        <button type="submit">Registrar Préstamo</button>
      </form>
    </div>
  );
};

export default FormularioPrestamo;
