import React, { useState } from 'react';
import '../Css/registroPrestamo.css';

const BankLoanForm = () => {
  const [formData, setFormData] = useState({
    monto: '',
    plazoMeses: '',
    fechaInicio: '',
    numeroPrestamo: '',
    tasaInteresMoratoria: '',
    estadoPrestamo: '',
    diaPago: '',
    IdClientes: '',
    clientesPersonaCedula: ''
  });

  const [errors, setErrors] = useState({});
  const [successMessage, setSuccessMessage] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const validateForm = () => {
    const newErrors = {};
    Object.keys(formData).forEach((key) => {
      if (!formData[key]) {
        newErrors[key] = 'Este campo es requerido';
      }
    });
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (validateForm()) {
      try {
        const response = await fetch('/crear-prestamo', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(formData),
        });

        if (!response.ok) {
          throw new Error('Error al enviar la solicitud');
        }

        const data = await response.json();
        setSuccessMessage('Préstamo registrado con éxito');
        console.log('Respuesta del servidor:', data);

        // Reiniciar el formulario después de enviar los datos
        setFormData({
          monto: '',
          plazoMeses: '',
          fechaInicio: '',
          numeroPrestamo: '',
          tasaInteresMoratoria: '',
          estadoPrestamo: '',
          diaPago: '',
          IdClientes: '',
          clientesPersonaCedula: ''
        });
        setErrors({});
      } catch (error) {
        console.error('Error al registrar el préstamo:', error);
      }
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-r from-teal-50 to-teal-100 flex items-center justify-center p-4">
      <div className="w-full max-w-2xl bg-white shadow-xl rounded-xl border-2 border-teal-300 p-8">
        <h2 className="text-3xl font-bold text-center text-teal-700 mb-6">
          Solicitud de Préstamo
        </h2>
        {successMessage && (
          <p className="text-center text-green-500 font-bold mb-4">
            {successMessage}
          </p>
        )}
        <div className="form-container bg-teal-50 p-6 rounded-lg shadow-lg border-2 border-teal-300">
          <form onSubmit={handleSubmit} className="space-y-4">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label htmlFor="monto" className="block text-teal-700 mb-2">
                  Monto del Préstamo
                </label>
                <input
                  type="number"
                  id="monto"
                  name="monto"
                  value={formData.monto}
                  onChange={handleChange}
                  className="w-full p-2 border rounded-lg border-teal-300 focus:outline-none focus:ring-2 focus:ring-teal-500"
                  placeholder="Ingrese monto"
                />
                {errors.monto && (
                  <p className="text-red-500 text-sm mt-1">{errors.monto}</p>
                )}
              </div>
              {/* Repite este patrón para los otros campos */}
            </div>
            <div className="flex justify-center mt-6">
              <button
                type="submit"
                className="bg-gradient-to-r from-teal-500 to-teal-600 text-white font-bold py-3 px-8 rounded-lg hover:from-teal-600 hover:to-teal-700 transition duration-300 ease-in-out transform hover:-translate-y-1 hover:scale-105"
              >
                Enviar Solicitud
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default BankLoanForm;
