import axios from "axios";

export const insertarPrestamo = async (prestamoNuevo) => {
  try {
    const {
      IdClientes,
      monto,
      plazoMeses,
      fechaInicio,
      numeroPrestamo,
      tasaInteresMoratoria,
      fechaVencimiento,
      diaPago,
      clientesPersonaCedula,
    } = prestamoNuevo;

    console.log("Datos enviados para insertar préstamo:", {
      IdClientes,
      monto,
      plazoMeses,
      fechaInicio,
      numeroPrestamo,
      tasaInteresMoratoria,
      fechaVencimiento,
      diaPago,
      clientesPersonaCedula,

    });

    // Insertar el nuevo préstamo
    const response = await axios.post("http://localhost:3333/prestamos/nuevoPrestamo", {
      monto,
      plazoMeses,
      fechaInicio,
      numeroPrestamo,
      tasaInteresMoratoria,
      fechaVencimiento,
      diaPago,
      IdClientes,
      clientesPersonaCedula,
    });

    console.log("Respuesta de la API (insertarPrestamo):", response.data);


    const ultimoPrestamoResponse = await axios.get("http://localhost:3333/prestamos/ultimoprestamo");
    
    console.log("Último número de préstamo obtenido:", ultimoPrestamoResponse.data.ultimoNumeroPrestamo);
    return {
      insertarPrestamo: response.data,
      ultimoPrestamo: ultimoPrestamoResponse.data.ultimoNumeroPrestamo
    };
  } catch (error) {
    if (error.response) {
      console.error("Error del servidor:", error.response.data);
    } else {
      console.error("Error al insertar el préstamo:", error.message);
    }
    throw error; 
  }
};
