import React, { useState, useEffect } from 'react';
import '../Css/registroPrestamo.css';
import { insertarPrestamo } from "../api/RegistrarPrestamo.api";

const BankLoanForm = () => {
  const [formData, setFormData] = useState({
    monto: '',
    plazoMeses: '',
    fechaInicio: '',
    fechaVencimiento: '',
    numeroPrestamo: '', // Lo dejamos vacío inicialmente
    tasaInteresMoratoria: 10,
    diaPago: '',
    clientesPersonaCedula: '',
    IdClientes: '',
    estadoPrestamo: 2
  });

  const [errors, setErrors] = useState({});
  
  // Para obtener el último número de préstamo cuando el componente se monta
  useEffect(() => {
    const fetchLastLoanNumber = async () => {
      try {
        const response = await axios.get("http://localhost:3333/prestamos/ultimoprestamo");
        setFormData(prevState => ({
          ...prevState,
          numeroPrestamo: response.data.ultimoNumeroPrestamo // Establecer el último número de préstamo obtenido
        }));
      } catch (error) {
        console.error("Error al obtener el último número de préstamo:", error);
        console.log(errors);
      }
    };

    fetchLastLoanNumber();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevState) => ({
      ...prevState,
      [name]: value
    }));
    if (name === 'plazoMeses' || name === 'fechaInicio') {
      calculateFechaVencimiento(value, name);
    }
  };

  const calculateFechaVencimiento = (value, field) => {
    let fechaInicio = formData.fechaInicio ? new Date(formData.fechaInicio) : null;
    let plazoMeses = formData.plazoMeses;

    if (field === 'plazoMeses') plazoMeses = value;
    if (field === 'fechaInicio') fechaInicio = value ? new Date(value) : null;

    if (fechaInicio && !isNaN(fechaInicio) && plazoMeses) {
      const fechaVencimiento = new Date(fechaInicio);
      fechaVencimiento.setMonth(fechaVencimiento.getMonth() + parseInt(plazoMeses, 10));

      setFormData((prevState) => ({
        ...prevState,
        fechaVencimiento: fechaVencimiento.toISOString().split('T')[0]
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};
    Object.keys(formData).forEach((key) => {
      if (!formData[key] && key !== 'fechaVencimiento') {
        newErrors[key] = 'Este campo es requerido';
      }
    });
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (validateForm()) {
      try {
        const prestamoNuevo = { ...formData, estadoPrestamo: '2' };
        const resultado = await insertarPrestamo(prestamoNuevo);  // Llamada a la función de la API
        console.log("Préstamo insertado exitosamente:", resultado);
      } catch (error) {
        console.error("Error al insertar el préstamo:", error);
      }
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-r from-teal-50 to-teal-100 flex items-center justify-center p-4">
      <div className="w-full max-w-2xl bg-white shadow-xl rounded-xl border-2 border-teal-300 p-8">
        <h2 className="text-3xl font-bold text-center text-teal-700 mb-6">Solicitud de Préstamo</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          {/* Número de préstamo */}
          <div>
            <label htmlFor="numeroPrestamo" className="block text-teal-700 mb-2">Número de Préstamo</label>
            <input
              type="text"
              id="numeroPrestamo"
              name="numeroPrestamo"
              value={formData.numeroPrestamo}
              onChange={handleChange}
              className="w-full p-2 border rounded-lg border-teal-300 focus:outline-none focus:ring-2 focus:ring-teal-500"
              placeholder="Número de préstamo generado"
              disabled
            />
          </div>

          {/* El resto de tu formulario... */}
          
          {/* Botón de envío */}
          <div className="text-center">
            <button
              type="submit"
              className="px-6 py-2 bg-teal-600 text-white rounded-lg shadow-lg hover:bg-teal-700 transition duration-200"
            >
              Enviar Solicitud
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default BankLoanForm;
