import axios from "axios";

const insertarPrestamo = async (prestamoNuevo) => {
    try {
      const {
        IdClientes,
        monto,
        plazoMeses,
        fechaInicio,
        numeroPrestamo,
        tasaInteresMoratoria,
        fechaVencimiento,
        diaPago,
        clientesPersonaCedula
      } = prestamoNuevo;
  
      const sql = `CALL agregarPrestamo(
        ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, @p_mensaje
      )`;
  
      const params = [
        null,  // idPrestamos se puede generar automáticamente o se puede dejar como NULL
        monto,
        plazoMeses,
        fechaInicio,
        numeroPrestamo,
        tasaInteresMoratoria,
        fechaVencimiento,
        diaPago,
        IdClientes,
        clientesPersonaCedula
      ];
  
      const connection = await mysql.createConnection(dbConfig);
      await connection.execute(sql, params);
      const [messageResult] = await connection.execute('SELECT @p_mensaje AS mensaje');
      console.log(messageResult[0].mensaje);
      return messageResult[0].mensaje;
    } catch (error) {
      console.error("Error al insertar el préstamo:", error);
      throw error;
    }
  };
