import React, { useState } from 'react';
import { InputText } from 'primereact/inputtext';
import { Password } from 'primereact/password';
import { Button } from 'primereact/button';
import { Toast } from 'primereact/toast';
import { useNavigate } from 'react-router-dom';
import './Css/Login.css';  // Estilos específicos para Login

const Login = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const toast = React.useRef(null);
    const navigate = useNavigate();

    const handleLogin = () => {
        if (username && password) {
            // Simula la autenticación
            if (username === 'admin' && password === '1234') {
                toast.current.show({ severity: 'success', summary: 'Login exitoso', detail: 'Bienvenido!', life: 3000 });
                setTimeout(() => navigate('/dashboard'), 1000);
            } else {
                toast.current.show({ severity: 'error', summary: 'Error', detail: 'Credenciales inválidas', life: 3000 });
            }
        } else {
            toast.current.show({ severity: 'warn', summary: 'Atención', detail: 'Por favor, completa todos los campos', life: 3000 });
        }
    };

    return (
        <div className="login-container">
            <Toast ref={toast} />
            <div className="card">
                <h2>Iniciar Sesión</h2>
                <div className="field">
                    <span className="p-float-label">
                        <InputText id="username" value={username} onChange={(e) => setUsername(e.target.value)} />
                        <label htmlFor="username">Usuario</label>
                    </span>
                </div>
                <div className="field">
                    <span className="p-float-label">
                        <Password id="password" value={password} onChange={(e) => setPassword(e.target.value)} toggleMask />
                        <label htmlFor="password">Contraseña</label>
                    </span>
                </div>
                <Button label="Ingresar" icon="pi pi-sign-in" onClick={handleLogin} />
            </div>
        </div>
    );
};

export default Login;
