import React, { useState } from "react";
import { Sidebar } from "primereact/sidebar";
import { Button } from "primereact/button";
import { useNavigate } from "react-router-dom";
import "primereact/resources/themes/lara-light-cyan/theme.css";
import "primereact/resources/primereact.min.css";
import "primeicons/primeicons.css";
import RegistrarCliente from "../Modals/RegistrarCliente";

const Navbar = () => {
  const [visible, setVisible] = useState(false); // Estado para controlar la visibilidad del Sidebar
  const [isModalVisible, setIsModalVisible] = useState(false); // Modal para registrar cliente
  const [menuActive, setMenuActive] = useState(false); // Estado para alternar menú
  const navigate = useNavigate();
  
  const handleCloseModal = () => {
    setIsModalVisible(false);
  };

  const handleRegister = (formData) => {
    console.log("Datos registrados:", formData);
    handleCloseModal();
  };

  return (
    <>
      <div className="login-wrapper">
        <div className="nav">
          {/* Botón de menú de hamburguesa */}
          <Button
            icon="pi pi-bars"
            onClick={() => setVisible(true)}
            className="p-button-text"
          />

          {/* Logo */}
          <div className="logo" style={{ display: "flex", alignItems: "center" }}>
            <img
              src="/img/navbar.png"
              alt="Logo"
              style={{ width: "40px", height: "40px", marginRight: "10px" }}
            />
            <span style={{ fontWeight: "bold", fontSize: "1.5rem", color: "#005f73" }}>
              Ouro Bank
            </span>
          </div>

          {/* Sidebar (Menú lateral) */}
          <Sidebar visible={visible} onHide={() => setVisible(false)} className="navbar-sidebar">
            <div className="navbar-menu">
              <Button
                label="Inicio"
                icon="pi pi-home"
                onClick={() => navigate("/")}
                className="p-button-text navbar-menu-item"
                style={{ width: "100%" }}
              />
              <Button
                label="Iniciar Sesión"
                icon="pi pi-sign-in"
                onClick={() => navigate("/IniciarSesion")}
                className="p-button-text navbar-menu-item"
                style={{ width: "100%", marginTop: "10px" }}
              />
              <Button
                label="Contáctenos"
                icon="pi pi-phone"
                onClick={() => navigate("/Contacto")}
                className="p-button-text navbar-menu-item"
                style={{ width: "100%", marginTop: "10px" }}
              />
            </div>
          </Sidebar>

          {/* Modal de registro */}
          <RegistrarCliente
            visible={isModalVisible}
            onHide={handleCloseModal}
            onRegister={handleRegister}
            className="join-btn"
          />
        </div>
      </div>
    </>
  );
};

export default Navbar;
