import React, { useState } from "react";
import { Button } from "primereact/button";
import { Dialog } from "primereact/dialog";
import { InputText } from "primereact/inputtext";
import { Password } from "primereact/password";

const Navbar = () => {
  const [isModalVisible, setIsModalVisible] = useState(false); // Estado del modal
  const [formData, setFormData] = useState({
    nombre: "",
    correo: "",
    contrasena: "",
  });

  const handleOpenModal = () => {
    setIsModalVisible(true);
  };

  const handleCloseModal = () => {
    setIsModalVisible(false);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleRegister = () => {
    if (Object.values(formData).some((value) => value.trim() === "")) {
      alert("Por favor, completa todos los campos.");
      return;
    }
    console.log("Datos registrados:", formData);
    setFormData({ nombre: "", correo: "", contrasena: "" }); // Resetea el formulario
    handleCloseModal();
  };

  return (
    <div className="login-wrapper">
      <div className="nav">
        <div className="logo">
          <img
            src="/img/navbar.png"
            alt="Logo"
            style={{ width: "40px", height: "40px" }}
          />
        </div>
        <div className="nav-links">
          <a href="#" className="nav-link">
            Inicio
          </a>
          <a href="#" className="nav-link">
            Opciones de Préstamo
          </a>
          <a href="#" className="nav-link">
            Contáctenos
          </a>
        </div>
        <Button
          label="Registrarse"
          className="join-btn"
          onClick={handleOpenModal} // Abre el modal
        />
      </div>

      {/* Modal dentro del mismo botón */}
      <Dialog
        header="Registrar Cliente"
        visible={isModalVisible}
        style={{ width: "50vw" }}
        onHide={handleCloseModal} // Cierra el modal
      >
        <div className="p-fluid">
          <div className="field">
            <label htmlFor="nombre">Nombre</label>
            <InputText
              id="nombre"
              name="nombre"
              value={formData.nombre}
              onChange={handleInputChange}
            />
          </div>
          <div className="field">
            <label htmlFor="correo">Correo Electrónico</label>
            <InputText
              id="correo"
              name="correo"
              value={formData.correo}
              onChange={handleInputChange}
            />
          </div>
          <div className="field">
            <label htmlFor="contrasena">Contraseña</label>
            <Password
              id="contrasena"
              name="contrasena"
              value={formData.contrasena}
              onChange={handleInputChange}
              toggleMask
            />
          </div>
          <Button
            label="Registrar"
            icon="pi pi-check"
            className="p-button-success"
            onClick={handleRegister} // Maneja el registro
          />
        </div>
      </Dialog>
    </div>
  );
};

export default Navbar;
