import React, { useState, useRef } from "react";
import { Dialog } from "primereact/dialog";
import { Button } from "primereact/button";
import { InputText } from "primereact/inputtext";
import { Password } from "primereact/password";
import { Toast } from "primereact/toast";
import "../Css/RegistrarCliente.css";

const RegistrarCliente = () => {
  const [visible, setVisible] = useState(false);
  const [formData, setFormData] = useState({
    cedula: "",
    nombre: "",
    primerApellido: "",
    segundoApellido: "",
    direccion: "",
    telefono: "",
    correoElectronico: "",
    contrasena: "",
  });

  const toast = useRef(null);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Validar solo números para cédula y teléfono
  const handleCedulaInput = (e) => {
    const regex = /^[0-9]{0,9}$/; // Permite solo hasta 9 dígitos numéricos
    if (!regex.test(e.target.value)) {
      e.preventDefault();
    }
  };

  const handleTelefonoInput = (e) => {
    const regex = /^[0-9]{0,15}$/; // Permite solo números
    if (!regex.test(e.target.value)) {
      e.preventDefault();
    }
  };

  // Validar solo letras para nombre y apellidos
  const handleNombreInput = (e) => {
    const regex = /^[A-Za-z\s]*$/; // Permite solo letras y espacios
    if (!regex.test(e.target.value)) {
      e.preventDefault();
    }
  };

  const validateForm = () => {
    // Validar cédula (solo números, exactamente 9 dígitos)
    const cedulaRegex = /^[0-9]{9}$/;
    if (!cedulaRegex.test(formData.cedula)) {
      toast.current.show({
        severity: "warn",
        summary: "Cédula inválida",
        detail: "La cédula debe contener solo 9 dígitos numéricos.",
      });
      return false;
    }

    // Validar teléfono (solo números)
    const telefonoRegex = /^[0-9]+$/;
    if (!telefonoRegex.test(formData.telefono)) {
      toast.current.show({
        severity: "warn",
        summary: "Teléfono inválido",
        detail: "El número de teléfono solo debe contener números.",
      });
      return false;
    }

    // Validar nombres y apellidos (solo letras)
    const letrasRegex = /^[A-Za-z\s]+$/;
    if (!letrasRegex.test(formData.nombre)) {
      toast.current.show({
        severity: "warn",
        summary: "Nombre inválido",
        detail: "El nombre solo debe contener letras.",
      });
      return false;
    }
    if (!letrasRegex.test(formData.primerApellido)) {
      toast.current.show({
        severity: "warn",
        summary: "Primer apellido inválido",
        detail: "El primer apellido solo debe contener letras.",
      });
      return false;
    }
    if (!letrasRegex.test(formData.segundoApellido)) {
      toast.current.show({
        severity: "warn",
        summary: "Segundo apellido inválido",
        detail: "El segundo apellido solo debe contener letras.",
      });
      return false;
    }

    // Validar que los demás campos no estén vacíos
    if (Object.values(formData).some((value) => value.trim() === "")) {
      toast.current.show({
        severity: "warn",
        summary: "Campos vacíos",
        detail: "Por favor, completa todos los campos",
      });
      return false;
    }

    return true;
  };

  const handleSubmit = () => {
    if (!validateForm()) {
      return;
    }

    console.log("Datos enviados:", formData);

    setFormData({
      cedula: "",
      nombre: "",
      primerApellido: "",
      segundoApellido: "",
      direccion: "",
      telefono: "",
      correoElectronico: "",
      contrasena: "",
    });

    toast.current.show({
      severity: "success",
      summary: "Registro exitoso",
      detail: "El usuario se ha registrado correctamente",
    });

    setVisible(false);
  };

  return (
    <div>
      <Toast ref={toast} />
      <Button label="Registrar Cliente" icon="pi pi-user-plus" onClick={() => setVisible(true)} />
      <Dialog
        header="Registro de Cliente"
        visible={visible}
        style={{ width: "50vw" }}
        onHide={() => setVisible(false)}
      >
        <div className="p-fluid">
          <div className="field">
            <label htmlFor="cedula">Digite su cédula</label>
            <InputText
              id="cedula"
              name="cedula"
              value={formData.cedula}
              onChange={handleInputChange}
              onInput={handleCedulaInput} // Llamar función para cédula
            />
          </div>
          <div className="field">
            <label htmlFor="nombre">Digite su nombre</label>
            <InputText
              id="nombre"
              name="nombre"
              value={formData.nombre}
              onChange={handleInputChange}
              onInput={handleNombreInput} // Llamar función para nombre
            />
          </div>
          <div className="field">
            <label htmlFor="primerApellido">Digite su primer apellido</label>
            <InputText
              id="primerApellido"
              name="primerApellido"
              value={formData.primerApellido}
              onChange={handleInputChange}
              onInput={handleNombreInput} // Llamar función para primer apellido
            />
          </div>
          <div className="field">
            <label htmlFor="segundoApellido">Digite su segundo apellido</label>
            <InputText
              id="segundoApellido"
              name="segundoApellido"
              value={formData.segundoApellido}
              onChange={handleInputChange}
              onInput={handleNombreInput} // Llamar función para segundo apellido
            />
          </div>
          <div className="field">
            <label htmlFor="direccion">Digite su dirección</label>
            <InputText
              id="direccion"
              name="direccion"
              value={formData.direccion}
              onChange={handleInputChange}
            />
          </div>
          <div className="field">
            <label htmlFor="telefono">Digite su número de teléfono</label>
            <InputText
              id="telefono"
              name="telefono"
              value={formData.telefono}
              onChange={handleInputChange}
              onInput={handleTelefonoInput} // Llamar función para teléfono
            />
          </div>
          <div className="field">
            <label htmlFor="correoElectronico">Digite su correo electrónico</label>
            <InputText
              id="correoElectronico"
              name="correoElectronico"
              value={formData.correoElectronico}
              onChange={handleInputChange}
            />
          </div>
          <div className="field">
            <label htmlFor="contrasena">Digite su contraseña</label>
            <Password
              id="contrasena"
              name="contrasena"
              value={formData.contrasena}
              onChange={handleInputChange}
              toggleMask
              feedback
              weakLabel="Muy débil"
              mediumLabel="Débil"
              strongLabel="Fuerte"
            />
          </div>
          <div className="p-mt-3">
            <Button label="Registrar" icon="pi pi-check" className="p-button-success" onClick={handleSubmit} />
          </div>
        </div>
      </Dialog>
    </div>
  );
};

export default RegistrarCliente;
