import React, { useState } from 'react';
import { Card } from 'primereact/card';
import { InputText } from 'primereact/inputtext';
import { Button } from 'primereact/button';
import { Calendar } from 'primereact/calendar';
import { InputNumber } from 'primereact/inputnumber';
import { classNames } from 'primereact/utils';

const BankLoanForm = () => {
  const [formData, setFormData] = useState({
    monto: null,
    plazoMeses: null,
    fechaInicio: null,
    numeroPrestamo: '',
    tasaInteresMoratoria: null,
    estadoPrestamo: '',
    diaPago: null,
    IdClientes: '',
    clientesPersonaCedula: ''
  });

  const [submitted, setSubmitted] = useState(false);

  const onInputChange = (e, name) => {
    const val = (e.target && e.target.value) || '';
    let _formData = {...formData};
    _formData[`${name}`] = val;

    setFormData(_formData);
  };

  const onNumberChange = (e, name) => {
    const val = e.value || null;
    let _formData = {...formData};
    _formData[`${name}`] = val;

    setFormData(_formData);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setSubmitted(true);
    
    // Aquí puedes agregar la lógica de envío del formulario
    console.log('Datos del préstamo:', formData);
  };

  const isFormFieldValid = (name) => {
    return !!(submitted && formData[name] === null);
  };

  const getFormErrorMessage = (name) => {
    return isFormFieldValid(name) && <small className="p-error">Este campo es requerido</small>;
  };

  return (
    <div className="surface-ground flex align-items-center justify-content-center min-h-screen">
      <Card 
        title="Solicitud de Préstamo" 
        className="w-full lg:w-6 p-6"
        style={{
          background: 'linear-gradient(135deg, #E6F3F3, #B0E0E6)',
          borderRadius: '15px',
          boxShadow: '0 4px 10px rgba(0,0,0,0.1)'
        }}
      >
        <form onSubmit={handleSubmit} className="p-fluid">
          <div className="grid formgrid">
            <div className="col-12 md:col-6">
              <div className="field">
                <label htmlFor="monto" className="block text-teal-700">Monto del Préstamo</label>
                <InputNumber
                  id="monto"
                  value={formData.monto}
                  onValueChange={(e) => onNumberChange(e, 'monto')}
                  mode="currency"
                  currency="USD"
                  className={classNames({ 'p-invalid': isFormFieldValid('monto') })}
                  placeholder="Ingrese monto"
                />
                {getFormErrorMessage('monto')}
              </div>
            </div>

            <div className="col-12 md:col-6">
              <div className="field">
                <label htmlFor="plazoMeses" className="block text-teal-700">Plazo (Meses)</label>
                <InputNumber
                  id="plazoMeses"
                  value={formData.plazoMeses}
                  onValueChange={(e) => onNumberChange(e, 'plazoMeses')}
                  className={classNames({ 'p-invalid': isFormFieldValid('plazoMeses') })}
                  placeholder="Número de meses"
                />
                {getFormErrorMessage('plazoMeses')}
              </div>
            </div>

            <div className="col-12 md:col-6">
              <div className="field">
                <label htmlFor="fechaInicio" className="block text-teal-700">Fecha de Inicio</label>
                <Calendar
                  id="fechaInicio"
                  value={formData.fechaInicio}
                  onChange={(e) => onInputChange(e, 'fechaInicio')}
                  dateFormat="dd/mm/yy"
                  className={classNames({ 'p-invalid': isFormFieldValid('fechaInicio') })}
                  placeholder="Seleccione fecha"
                />
                {getFormErrorMessage('fechaInicio')}
              </div>
            </div>

            <div className="col-12 md:col-6">
              <div className="field">
                <label htmlFor="numeroPrestamo" className="block text-teal-700">Número de Préstamo</label>
                <InputText
                  id="numeroPrestamo"
                  value={formData.numeroPrestamo}
                  onChange={(e) => onInputChange(e, 'numeroPrestamo')}
                  className={classNames({ 'p-invalid': isFormFieldValid('numeroPrestamo') })}
                  placeholder="Número único de préstamo"
                />
                {getFormErrorMessage('numeroPrestamo')}
              </div>
            </div>

            <div className="col-12 md:col-6">
              <div className="field">
                <label htmlFor="tasaInteresMoratoria" className="block text-teal-700">Tasa de Interés Moratorio</label>
                <InputNumber
                  id="tasaInteresMoratoria"
                  value={formData.tasaInteresMoratoria}
                  onValueChange={(e) => onNumberChange(e, 'tasaInteresMoratoria')}
                  mode="decimal"
                  minFractionDigits={2}
                  maxFractionDigits={2}
                  className={classNames({ 'p-invalid': isFormFieldValid('tasaInteresMoratoria') })}
                  placeholder="Tasa de interés"
                />
                {getFormErrorMessage('tasaInteresMoratoria')}
              </div>
            </div>

            <div className="col-12 md:col-6">
              <div className="field">
                <label htmlFor="estadoPrestamo" className="block text-teal-700">Estado del Préstamo</label>
                <InputText
                  id="estadoPrestamo"
                  value={formData.estadoPrestamo}
                  onChange={(e) => onInputChange(e, 'estadoPrestamo')}
                  className={classNames({ 'p-invalid': isFormFieldValid('estadoPrestamo') })}
                  placeholder="Estado actual"
                />
                {getFormErrorMessage('estadoPrestamo')}
              </div>
            </div>

            <div className="col-12 md:col-6">
              <div className="field">
                <label htmlFor="diaPago" className="block text-teal-700">Día de Pago</label>
                <InputNumber
                  id="diaPago"
                  value={formData.diaPago}
                  onValueChange={(e) => onNumberChange(e, 'diaPago')}
                  min={1}
                  max={31}
                  className={classNames({ 'p-invalid': isFormFieldValid('diaPago') })}
                  placeholder="Día del mes"
                />
                {getFormErrorMessage('diaPago')}
              </div>
            </div>

            <div className="col-12 md:col-6">
              <div className="field">
                <label htmlFor="IdClientes" className="block text-teal-700">ID de Cliente</label>
                <InputText
                  id="IdClientes"
                  value={formData.IdClientes}
                  onChange={(e) => onInputChange(e, 'IdClientes')}
                  className={classNames({ 'p-invalid': isFormFieldValid('IdClientes') })}
                  placeholder="Identificador de cliente"
                />
                {getFormErrorMessage('IdClientes')}
              </div>
            </div>

            <div className="col-12 md:col-6">
              <div className="field">
                <label htmlFor="clientesPersonaCedula" className="block text-teal-700">Cédula del Cliente</label>
                <InputText
                  id="clientesPersonaCedula"
                  value={formData.clientesPersonaCedula}
                  onChange={(e) => onInputChange(e, 'clientesPersonaCedula')}
                  className={classNames({ 'p-invalid': isFormFieldValid('clientesPersonaCedula') })}
                  placeholder="Número de identificación"
                />
                {getFormErrorMessage('clientesPersonaCedula')}
              </div>
            </div>
          </div>

          <div className="flex justify-content-center mt-4">
            <Button 
              type="submit" 
              label="Enviar Solicitud" 
              className="p-button-raised p-button-rounded"
              style={{
                background: 'linear-gradient(to right, #0fa2a2, #14b8a6)',
                border: 'none'
              }}
            />
          </div>
        </form>
      </Card>
    </div>
  );
};

export default BankLoanForm;
