import React, { useState } from 'react';
import '../Css/registroPrestamo.css';

const BankLoanForm = () => {
  const [formData, setFormData] = useState({
    monto: '',
    plazoMeses: '',
    fechaInicio: '',
    fechaVencimiento: '',
    numeroPrestamo: '',
    tasaInteresMoratoria: 10, // Definido desde el código (por ejemplo, 10%)
    diaPago: '',
    clientesPersonaCedula: ''
  });

  const [errors, setErrors] = useState({});

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevState) => ({
      ...prevState,
      [name]: value
    }));
    if (name === 'plazoMeses' || name === 'fechaInicio') {
      calculateFechaVencimiento(value, name);
    }
  };

  const calculateFechaVencimiento = (value, field) => {
    let fechaInicio = formData.fechaInicio ? new Date(formData.fechaInicio) : null;
    let plazoMeses = formData.plazoMeses;
  
    if (field === 'plazoMeses') plazoMeses = value;
    if (field === 'fechaInicio') fechaInicio = value ? new Date(value) : null;
  
    if (fechaInicio && !isNaN(fechaInicio) && plazoMeses) {
      const fechaVencimiento = new Date(fechaInicio);
      fechaVencimiento.setMonth(fechaVencimiento.getMonth() + parseInt(plazoMeses, 10));
  
      setFormData((prevState) => ({
        ...prevState,
        fechaVencimiento: fechaVencimiento.toISOString().split('T')[0] // Formato YYYY-MM-DD
      }));
    }
  };
  

  const validateForm = () => {
    const newErrors = {};
    Object.keys(formData).forEach((key) => {
      if (!formData[key] && key !== 'fechaVencimiento') {
        newErrors[key] = 'Este campo es requerido';
      }
    });
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      console.log('Datos del préstamo:', formData);
      // Aquí iría la lógica de envío del formulario
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-r from-teal-50 to-teal-100 flex items-center justify-center p-4">
      <div className="w-full max-w-2xl bg-white shadow-xl rounded-xl border-2 border-teal-300 p-8">
        <h2 className="text-3xl font-bold text-center text-teal-700 mb-6">
          Solicitud de Préstamo
        </h2>
        <div className="form-container bg-teal-50 p-6 rounded-lg shadow-lg border-2 border-teal-300">
          <form onSubmit={handleSubmit} className="space-y-4">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {/* Monto */}
              <div>
                <label htmlFor="monto" className="block text-teal-700 mb-2">Monto del Préstamo</label>
                <input
                  type="number"
                  id="monto"
                  name="monto"
                  value={formData.monto}
                  onChange={handleChange}
                  className="w-full p-2 border rounded-lg border-teal-300 focus:outline-none focus:ring-2 focus:ring-teal-500"
                  placeholder="Ingrese monto"
                />
                {errors.monto && <p className="text-red-500 text-sm mt-1">{errors.monto}</p>}
              </div>

              {/* Plazo Meses */}
              <div>
                <label htmlFor="plazoMeses" className="block text-teal-700 mb-2">Plazo (Meses)</label>
                <select
                  id="plazoMeses"
                  name="plazoMeses"
                  value={formData.plazoMeses}
                  onChange={handleChange}
                  className="w-full p-2 border rounded-lg border-teal-300 focus:outline-none focus:ring-2 focus:ring-teal-500"
                >
                  <option value="">Seleccione plazo</option>
                  {[6, 12, 18, 24, 36, 48, 60].map((mes) => (
                    <option key={mes} value={mes}>{mes} meses</option>
                  ))}
                </select>
                {errors.plazoMeses && <p className="text-red-500 text-sm mt-1">{errors.plazoMeses}</p>}
              </div>

              {/* Fecha Inicio */}
              <div>
                <label htmlFor="fechaInicio" className="block text-teal-700 mb-2">Fecha de Inicio</label>
                <input
                  type="date"
                  id="fechaInicio"
                  name="fechaInicio"
                  value={formData.fechaInicio}
                  onChange={handleChange}
                  className="w-full p-2 border rounded-lg border-teal-300 focus:outline-none focus:ring-2 focus:ring-teal-500"
                />
                {errors.fechaInicio && <p className="text-red-500 text-sm mt-1">{errors.fechaInicio}</p>}
              </div>

              {/* Fecha Vencimiento */}
              <div>
                <label htmlFor="fechaVencimiento" className="block text-teal-700 mb-2">Fecha de Vencimiento</label>
                <input
                  type="date"
                  id="fechaVencimiento"
                  name="fechaVencimiento"
                  value={formData.fechaVencimiento}
                  readOnly
                  disabled
                  className="w-full p-2 border rounded-lg border-teal-300 bg-gray-100 cursor-not-allowed"
                />
              </div>

              {/* Tasa Interés Moratoria */}
              <div>
                <label htmlFor="tasaInteresMoratoria" className="block text-teal-700 mb-2">Tasa de Interés</label>
                <input
                  type="number"
                  id="tasaInteresMoratoria"
                  name="tasaInteresMoratoria"
                  value={formData.tasaInteresMoratoria}
                  readOnly
                  disabled
                  className="w-full p-2 border rounded-lg border-teal-300 bg-gray-100 cursor-not-allowed"
                />
              </div>

              {/* Día de Pago */}
              <div>
                <label htmlFor="diaPago" className="block text-teal-700 mb-2">Día de Pago</label>
                <input
                  type="number"
                  id="diaPago"
                  name="diaPago"
                  value={formData.diaPago}
                  onChange={handleChange}
                  min="1"
                  max="31"
                  className="w-full p-2 border rounded-lg border-teal-300 focus:outline-none focus:ring-2 focus:ring-teal-500"
                  placeholder="Día del mes"
                />
                {errors.diaPago && <p className="text-red-500 text-sm mt-1">{errors.diaPago}</p>}
              </div>

              {/* Cédula Cliente */}
              <div>
                <label htmlFor="clientesPersonaCedula" className="block text-teal-700 mb-2">Cédula del Cliente</label>
                <input
                  type="text"
                  id="clientesPersonaCedula"
                  name="clientesPersonaCedula"
                  value={formData.clientesPersonaCedula}
                  onChange={handleChange}
                  className="w-full p-2 border rounded-lg border-teal-300 focus:outline-none focus:ring-2 focus:ring-teal-500"
                  placeholder="Número de identificación"
                />
                {errors.clientesPersonaCedula && <p className="text-red-500 text-sm mt-1">{errors.clientesPersonaCedula}</p>}
              </div>
            </div>

            {/* Submit Button */}
            <div className="flex justify-center mt-6">
              <button
                type="submit"
                className="bg-gradient-to-r from-teal-500 to-teal-600 text-white font-bold py-3 px-8 rounded-lg hover:from-teal-600 hover:to-teal-700 transition duration-300 ease-in-out transform hover:-translate-y-1 hover:scale-105"
              >
                Enviar Solicitud
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default BankLoanForm;
