import axios from "axios";

export const insertarCliente = async (clienteNuevo) => {
  try {
    const { nombre, primerApellido, segundoApellido, personaCedula, direccion, telefono, correoElectronico, contrasena } = clienteNuevo;
    console.log("Datos enviados para insertar cliente:", { nombre, primerApellido, segundoApellido, personaCedula, direccion, telefono, correoElectronico, contrasena });
    const response = await axios.post(
      "http://localhost:3333/cliente/crear-cliente",
      {
        nombre,
        primerApellido,
        segundoApellido,
        personaCedula,
        direccion,
        telefono,
        correoElectronico,
        contrasena
      }
    );
    console.log("Respuesta de la API:", response.data); 
    return response.data;
  } catch (error) {
    console.error("Error al insertar el cliente:", error);
    throw error;
  }
};


export const obtenerClientes = async () => {
  try {
    const response = await axios.get("http://localhost:3333/cliente/obtener-clientes");
    return response.data;
  } catch (error) {
    console.error("Error al obtener la lista de clientes:", error);
    throw error;
  }
};


export const eliminarCliente = async (idClientes) => {
  try {
    const response = await axios.delete(`http://localhost:3333/cliente/eliminarCliente/${idClientes}`);
    return response.data;
  } catch (error) {
    console.error("Error al eliminar el cliente:", error);
    throw error;
  }
};


export const modificarCliente = async (idClientes, clienteActualizado) => {
  try {
    const { nombre, primerApellido, segundoApellido, direccion, telefono, correoElectronico, contrasena } = clienteActualizado;
    console.log("Datos enviados para modificar cliente:", {
      nombre,
      primerApellido,
      segundoApellido,
      direccion,
      telefono,
      correoElectronico,
      contrasena
    });
    const response = await axios.put(
      `http://localhost:3333/cliente/modificarCliente/${idClientes}`,
      {
        nombre,
        primerApellido,
        segundoApellido,
        direccion,
        telefono,
        correoElectronico,
        contrasena
      }
    );
    console.log("Respuesta de la API:", response.data); 
    return response.data;
  } catch (error) {
    console.error("Error al modificar el cliente:", error);
    throw error;
  }
};
