import React, { useState, useEffect, useRef } from 'react';  // Importación corregida
import '../Css/registroPrestamo.css';
import { insertarPrestamo } from "../api/RegistrarPrestamo.api";
import axios from 'axios';
import { Toast } from 'primereact/toast';

const BankLoanForm = () => {
  const [formData, setFormData] = useState({
    monto: '',
    plazoMeses: '',
    fechaInicio: '',
    fechaVencimiento: '',
    numeroPrestamo: '', // Inicialmente vacío
    tasaInteresMoratoria: 10,
    diaPago: '', // Campo numérico para día de pago
    clientesPersonaCedula: '',
    IdClientes: '', // Campo para el ID del cliente
    estadoPrestamo: 2  // Establecer estado predeterminado a 2
  });

  const [errors, setErrors] = useState({});
  const toast = useRef(null);  // Crear una referencia para el Toast

  useEffect(() => {
    const fetchLastLoanNumber = async () => {
      try {
        const response = await axios.get("http://localhost:3333/prestamos/obtenerultimo");
        console.log("Respuesta de la API:", response.data);

        const ultimoPrestamo = response.data.ultimoPrestamo || 0;
        const randomNumber = Math.floor(Math.random() * 100) + 1;

        const nuevoNumeroPrestamo = ultimoPrestamo + randomNumber;

        setFormData(prevState => ({
          ...prevState,
          numeroPrestamo: nuevoNumeroPrestamo
        }));
      } catch (error) {
        console.error("Error al obtener el último número de préstamo:", error);
      }
    };
    fetchLastLoanNumber();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevState) => ({
      ...prevState,
      [name]: value
    }));
    if (name === 'plazoMeses' || name === 'fechaInicio') {
      calculateFechaVencimiento(value, name);
    }
  };

  const calculateFechaVencimiento = (value, field) => {
    let fechaInicio = formData.fechaInicio ? new Date(formData.fechaInicio) : null;
    let plazoMeses = formData.plazoMeses;

    if (field === 'plazoMeses') plazoMeses = value;
    if (field === 'fechaInicio') fechaInicio = value ? new Date(value) : null;

    if (fechaInicio && !isNaN(fechaInicio) && plazoMeses) {
      const fechaVencimiento = new Date(fechaInicio);
      fechaVencimiento.setMonth(fechaVencimiento.getMonth() + parseInt(plazoMeses, 10));

      setFormData((prevState) => ({
        ...prevState,
        fechaVencimiento: fechaVencimiento.toISOString().split('T')[0]
      }));
    }
  };

  const validateForm = () => {
    const newErrors = {};
    Object.keys(formData).forEach((key) => {
      if (!formData[key] && key !== 'fechaVencimiento') {
        newErrors[key] = 'Este campo es requerido';
      }
    });
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (validateForm()) {
      try {
        const prestamoNuevo = {
          ...formData,
          estadoPrestamo: 2,
        };
        const resultado = await insertarPrestamo(prestamoNuevo);

        console.log("Préstamo insertado exitosamente:", resultado);
        toast.current.show({ severity: 'success', summary: 'Exitoso', detail: 'Préstamo registrado correctamente', life: 3000 });  // Mostrar Toast de éxito

      } catch (error) {
        console.error("Error al insertar el préstamo:", error);
        toast.current.show({ severity: 'error', summary: 'Error', detail: 'Hubo un problema al registrar el préstamo', life: 3000 });  // Mostrar Toast de error
      }
    }
  };

  return (
<div className="w-full max-w-2xl p-6 bg-white rounded-lg shadow-lg border-2 border-teal-400">
  <h2 className="text-center text-2xl font-bold mb-6">Registro de Préstamo</h2>
  <form onSubmit={handleSubmit(onSubmit)}>

    {/* Contenedor para los campos, ajustado para tres columnas en pantallas grandes */}
    <div className="grid grid-cols-1 md:grid-cols-3 gap-6">

      <div className="mb-6">
        <label htmlFor="monto" className="block text-gray-700">Monto</label>
        <input
          type="number"
          id="monto"
          name="monto"
          {...register("monto", { required: "Este campo es obligatorio" })}
          className="input"
        />
        {errors.monto && <p className="text-red-500">{errors.monto.message}</p>}
      </div>

      <div className="mb-6">
        <label htmlFor="plazo" className="block text-gray-700">Plazo (Meses)</label>
        <input
          type="number"
          id="plazo"
          name="plazo"
          {...register("plazo", { required: "Este campo es obligatorio" })}
          className="input"
        />
        {errors.plazo && <p className="text-red-500">{errors.plazo.message}</p>}
      </div>

      <div className="mb-6">
        <label htmlFor="interes" className="block text-gray-700">Interés (%)</label>
        <input
          type="number"
          id="interes"
          name="interes"
          {...register("interes", { required: "Este campo es obligatorio" })}
          className="input"
        />
        {errors.interes && <p className="text-red-500">{errors.interes.message}</p>}
      </div>

      <div className="mb-6">
        <label htmlFor="diaPago" className="block text-gray-700">Día de Pago</label>
        <input
          type="date"
          id="diaPago"
          name="diaPago"
          {...register("diaPago", { required: "Este campo es obligatorio" })}
          className="input"
        />
        {errors.diaPago && <p className="text-red-500">{errors.diaPago.message}</p>}
      </div>

    </div>

    <div className="flex justify-center mt-6">
      <button
        type="submit"
        className="bg-teal-500 text-white p-2 rounded-lg hover:bg-teal-600 focus:outline-none focus:ring-2 focus:ring-teal-500 focus:ring-opacity-50"
      >
        Registrar Préstamo
      </button>
    </div>
  </form>
    </div >
  );
};

export default BankLoanForm;
