import { Toast } from 'primereact/toast';
import React, { useState, useEffect, useRef } from 'react';
import '../Css/registroPrestamo.css';
import { insertarPrestamo } from "../api/RegistrarPrestamo.api";
import axios from 'axios';

const BankLoanForm = () => {
  // Inicializamos el estado del formulario con campos vacíos
  const [formData, setFormData] = useState({
    monto: '',
    plazoMeses: '',
    fechaInicio: '',
    fechaVencimiento: '',
    numeroPrestamo: '',
    tasaInteresMoratoria: 10,
    diaPago: '',
    clientesPersonaCedula: '',  // Dejar vacío, el usuario deberá ingresar este dato
    IdClientes: '', // Dejar vacío, el usuario deberá ingresar este dato
    estadoPrestamo: 2
  });

  const [errors, setErrors] = useState({});
  const toast = useRef(null);

  // Recuperar el último número de préstamo desde la base de datos
  useEffect(() => {
    const fetchLastLoanNumber = async () => {
      try {
        const response = await axios.get("http://localhost:3333/prestamos/obtenerultimo");
        const ultimoPrestamo = response.data.ultimoPrestamo || 0;
        const randomNumber = Math.floor(Math.random() * 100) + 1;
        const nuevoNumeroPrestamo = ultimoPrestamo + randomNumber;

        setFormData(prevState => ({
          ...prevState,
          numeroPrestamo: nuevoNumeroPrestamo
        }));
      } catch (error) {
        console.error("Error al obtener el último número de préstamo:", error);
      }
    };

    fetchLastLoanNumber();
  }, []);

  // Manejo de cambios en los campos del formulario
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevState) => ({
      ...prevState,
      [name]: value
    }));
    if (name === 'plazoMeses' || name === 'fechaInicio') {
      calculateFechaVencimiento(value, name);
    }
  };

  // Cálculo de la fecha de vencimiento en base al plazo de meses
  const calculateFechaVencimiento = (value, field) => {
    let fechaInicio = formData.fechaInicio ? new Date(formData.fechaInicio) : null;
    let plazoMeses = formData.plazoMeses;

    if (field === 'plazoMeses') plazoMeses = value;
    if (field === 'fechaInicio') fechaInicio = value ? new Date(value) : null;

    if (fechaInicio && !isNaN(fechaInicio) && plazoMeses) {
      const fechaVencimiento = new Date(fechaInicio);
      fechaVencimiento.setMonth(fechaVencimiento.getMonth() + parseInt(plazoMeses, 10));

      setFormData((prevState) => ({
        ...prevState,
        fechaVencimiento: fechaVencimiento.toISOString().split('T')[0]
      }));
    }
  };

  // Validación del formulario
  const validateForm = () => {
    const newErrors = {};
    Object.keys(formData).forEach((key) => {
      if (!formData[key] && key !== 'fechaVencimiento') {
        newErrors[key] = 'Este campo es requerido';
      }
    });
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // Envío del formulario
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (validateForm()) {
      try {
        const prestamoNuevo = {
          ...formData,
          estadoPrestamo: 2, 
        };
        const resultado = await insertarPrestamo(prestamoNuevo);
        console.log(resultado);
        toast.current.show({ severity: 'success', summary: 'Éxito', detail: 'Préstamo registrado con éxito', life: 3000 });
      } catch (error) {
        
        console.error("Error al insertar el préstamo:", error);
        toast.current.show({ severity: 'error', summary: 'Error', detail: 'No se pudo registrar el préstamo', life: 3000 });
      }
      
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-r from-teal-50 to-teal-100 flex items-center justify-center p-4">
      <div className="w-full max-w-2xl bg-white shadow-xl rounded-xl border-2 border-teal-300 p-8">
        <h2 className="text-3xl font-bold text-center text-teal-700 mb-6">Solicitud de Préstamo</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          {/* Número de préstamo */}
          <div>
            <label htmlFor="numeroPrestamo" className="block text-teal-700 mb-2">Número de Préstamo</label>
            <input
              type="text"
              id="numeroPrestamo"
              name="numeroPrestamo"
              value={formData.numeroPrestamo}
              onChange={handleChange}
              className="w-full p-2 border rounded-lg border-teal-300 focus:outline-none focus:ring-2 focus:ring-teal-500"
              placeholder="Número de préstamo generado"
              disabled
            />
          </div>

          {/* Monto */}
          <div>
            <label htmlFor="monto" className="block text-teal-700 mb-2">Monto del Préstamo</label>
            <input
              type="number"
              id="monto"
              name="monto"
              value={formData.monto}
              onChange={handleChange}
              className="w-full p-2 border rounded-lg border-teal-300 focus:outline-none focus:ring-2 focus:ring-teal-500"
              placeholder="Ingrese monto"
            />
            {errors.monto && <p className="text-red-500 text-sm mt-1">{errors.monto}</p>}
          </div>

          {/* Plazo en meses */}
          <div>
            <label htmlFor="plazoMeses" className="block text-teal-700 mb-2">Plazo (meses)</label>
            <input
              type="number"
              id="plazoMeses"
              name="plazoMeses"
              value={formData.plazoMeses}
              onChange={handleChange}
              className="w-full p-2 border rounded-lg border-teal-300 focus:outline-none focus:ring-2 focus:ring-teal-500"
              placeholder="Ingrese plazo en meses"
            />
            {errors.plazoMeses && <p className="text-red-500 text-sm mt-1">{errors.plazoMeses}</p>}
          </div>

          {/* Fecha de inicio */}
          <div>
            <label htmlFor="fechaInicio" className="block text-teal-700 mb-2">Fecha de Inicio</label>
            <input
              type="date"
              id="fechaInicio"
              name="fechaInicio"
              value={formData.fechaInicio}
              onChange={handleChange}
              className="w-full p-2 border rounded-lg border-teal-300 focus:outline-none focus:ring-2 focus:ring-teal-500"
            />
            {errors.fechaInicio && <p className="text-red-500 text-sm mt-1">{errors.fechaInicio}</p>}
          </div>

          {/* Fecha de vencimiento */}
          <div>
            <label htmlFor="fechaVencimiento" className="block text-teal-700 mb-2">Fecha de Vencimiento</label>
            <input
              type="date"
              id="fechaVencimiento"
              name="fechaVencimiento"
              value={formData.fechaVencimiento}
              onChange={handleChange}
              className="w-full p-2 border rounded-lg border-teal-300 focus:outline-none focus:ring-2 focus:ring-teal-500"
              disabled
            />
          </div>

          {/* Cédula del cliente */}
          <div>
            <label htmlFor="clientesPersonaCedula" className="block text-teal-700 mb-2">Cédula del Cliente</label>
            <input
              type="text"
              id="clientesPersonaCedula"
              name="clientesPersonaCedula"
              value={formData.clientesPersonaCedula}
              onChange={handleChange}
              className="w-full p-2 border rounded-lg border-teal-300 focus:outline-none focus:ring-2 focus:ring-teal-500"
              placeholder="Ingrese la cédula del cliente"
            />
            {errors.clientesPersonaCedula && <p className="text-red-500 text-sm mt-1">{errors.clientesPersonaCedula}</p>}
          </div>

          {/* ID del cliente */}
          <div>
            <label htmlFor="IdClientes" className="block text-teal-700 mb-2">ID Cliente</label>
            <input
              type="text"
              id="IdClientes"
              name="IdClientes"
              value={formData.IdClientes}
              onChange={handleChange}
              className="w-full p-2 border rounded-lg border-teal-300 focus:outline-none focus:ring-2 focus:ring-teal-500"
              placeholder="Ingrese el ID del cliente"
            />
            {errors.IdClientes && <p className="text-red-500 text-sm mt-1">{errors.IdClientes}</p>}
          </div>

          <button
            type="submit"
            className="w-full bg-teal-600 text-white py-2 rounded-lg hover:bg-teal-700 transition duration-300"
          >
            Registrar Préstamo
          </button>
        </form>
      </div>
      <Toast ref={toast} />
    </div>
  );
};

export default BankLoanForm;
